<?phpnamespace ts\base;use Yii;/** * This is the base Controller. * * @author Haisen <thirsight@gmail.com> * @since 1.0 */class BaseController extends \yii\web\Controller{    /**     * delete 操作必须为 post     *     * @return array     */    public function behaviors()    {        return [            'verbs' => [                'class' => \yii\filters\VerbFilter::className(),                'actions' => [                    'delete' => ['post'],                ],            ],        ];    }}