<?phpnamespace ts\base;use Yii;/** * This is the base model. * * @author Haisen <thirsight@gmail.com> * @since 1.0 */class BaseModel extends \yii\db\ActiveRecord{    /**     * @inheritdoc     */    public function behaviors()    {        return [            \yii\behaviors\TimestampBehavior::className(),        ];    }    /**     * @param $attributeId     * @param $attributeSort     * @param $ids     * @return int|null     */    public static function updateAutoSort($attributeId, $attributeSort, $ids)    {        if (is_string($ids) && strstr($ids, ',')) {            $ids = explode(',', $ids);        }        if (!empty($ids) && is_array($ids)) {            $sql = '';            $params = [];            foreach ($ids as $sort => $id) {                $sort++;                $sql .= "UPDATE " . static::tableName() . " SET `{$attributeSort}` = :sort{$sort} WHERE `{$attributeId}` = :id{$sort};\n";                $params[":sort{$sort}"] = $sort;                $params[":id{$sort}"] = $id;            }            return static::getDb()->createCommand($sql, $params)->execute();        }        return null;    }}